 * extension Bedrock.LogLevel {
 * func toCoreLevel() -> WorldAppCore.LogLevel {
 * switch self {
 * case .debug, .trace:
 * return .debug
 * case .info:
 * return .info
 * case .error:
 * return .error
 * case .warn:
 * return .warn
 * }
 * }
 * }
